"use client"

import { useState, useEffect } from "react"
import { Container, Row, Col, Card, Button, Form, Alert } from "react-bootstrap"
import { useParams, useNavigate } from "react-router-dom"
import { assessmentService } from "../services/api"
import { useAuth } from "../contexts/AuthContext"

const AssessmentPage = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const { user } = useAuth()
  const [assessment, setAssessment] = useState(null)
  const [answers, setAnswers] = useState({})
  const [currentQuestion, setCurrentQuestion] = useState(0)
  const [submitted, setSubmitted] = useState(false)
  const [result, setResult] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [viewMode, setViewMode] = useState(false)

  useEffect(() => {
    if (id && id !== 'undefined') {
      fetchAssessment()
      // If user is an instructor, set to view mode
      if (user && user.role === 'Instructor') {
        setViewMode(true)
      }
    } else {
      setError("Invalid assessment ID")
      setLoading(false)
    }
  }, [id, user])

  const fetchAssessment = async () => {
    try {
      const response = await assessmentService.getAssessmentById(id)
      console.log("Assessment data:", response.data)

      if (response.data) {
        const assessmentData = response.data

        // Parse questions if they're stored as a JSON string
        if (typeof assessmentData.questions === 'string') {
          try {
            assessmentData.questions = JSON.parse(assessmentData.questions)
            console.log("Parsed questions:", assessmentData.questions)
          } catch (e) {
            console.error("Error parsing questions:", e)
            assessmentData.questions = []
          }
        }

        // Ensure questions is an array
        if (!Array.isArray(assessmentData.questions)) {
          console.log("Questions is not an array, creating mock questions for testing")
          // Create mock questions for testing if none exist
          assessmentData.questions = [
            {
              id: "q1",
              text: "What is the capital of France?",
              options: [
                { id: "a", text: "London" },
                { id: "b", text: "Paris" },
                { id: "c", text: "Berlin" },
                { id: "d", text: "Madrid" }
              ],
              correctOption: "b"
            },
            {
              id: "q2",
              text: "Which planet is known as the Red Planet?",
              options: [
                { id: "a", text: "Venus" },
                { id: "b", text: "Mars" },
                { id: "c", text: "Jupiter" },
                { id: "d", text: "Saturn" }
              ],
              correctOption: "b"
            }
          ]
        }

        // Format questions if they don't have the expected structure
        assessmentData.questions = assessmentData.questions.map((q, index) => {
          // If question doesn't have an id, add one
          if (!q.id) q.id = `q${index + 1}`;

          // If question doesn't have text but has questionText
          if (!q.text && q.questionText) q.text = q.questionText;
          // If neither exists, provide default text
          if (!q.text && !q.questionText) q.text = `Question ${index + 1}`;

          // Ensure options are in the correct format
          if (!q.options || !Array.isArray(q.options) || q.options.length === 0) {
            q.options = [
              { id: "a", text: "Option A" },
              { id: "b", text: "Option B" },
              { id: "c", text: "Option C" },
              { id: "d", text: "Option D" }
            ];
          }

          // Ensure each option has id and text properties
          q.options = q.options.map((opt, optIndex) => {
            if (typeof opt === 'string') {
              return { id: `opt${optIndex}`, text: opt };
            }
            if (!opt.id) opt.id = `opt${optIndex}`;
            if (!opt.text && opt.optionText) opt.text = opt.optionText;
            if (!opt.text) opt.text = `Option ${optIndex + 1}`;
            return opt;
          });

          return q;
        });

        console.log("Processed assessment data:", assessmentData);
        setAssessment(assessmentData);
      } else {
        setError("Assessment not found")
      }
    } catch (error) {
      console.error("Error fetching assessment:", error)
      setError("Error loading assessment data")
    } finally {
      setLoading(false)
    }
  }

  const handleAnswerChange = (questionId, optionId) => {
    setAnswers({
      ...answers,
      [questionId]: optionId,
    })
  }

  const clearCurrentAnswer = () => {
    if (!assessment) return;

    const currentQuestionId = assessment.questions[currentQuestion].id;
    const updatedAnswers = { ...answers };
    delete updatedAnswers[currentQuestionId];
    setAnswers(updatedAnswers);
  }

  // Calculate score based on correct answers
  const calculateScore = () => {
    if (!assessment || !assessment.questions) return { score: 0, maxScore: 0, percentage: 0 };

    const totalQuestions = assessment.questions.length;
    const maxScore = assessment.maxScore || 100; // Use assessment's maxScore or default to 100

    // Count correct answers
    let correctAnswers = 0;
    assessment.questions.forEach(question => {
      const userAnswer = answers[question.id];

      // Check if user's answer matches the correct answer
      if (userAnswer && question.correctOption && userAnswer === question.correctOption) {
        correctAnswers++;
      }
    });

    console.log(`Correct answers: ${correctAnswers} out of ${totalQuestions}`);

    // Calculate score based on the assessment's maxScore and number of questions
    const scorePerQuestion = maxScore / totalQuestions;
    const score = Math.round(correctAnswers * scorePerQuestion);
    const percentage = Math.round((correctAnswers / totalQuestions) * 100);

    return {
      score,
      maxScore,
      percentage,
      correctAnswers,
      totalQuestions,
      passed: percentage >= 60 // Pass threshold of 60%
    };
  }

  const handleSubmit = async () => {
    try {
      setLoading(true);

      // Submit answers to backend for scoring
      const response = await assessmentService.submitAssessment(id, answers);

      // Use the result from the backend
      const calculatedResult = {
        score: response.data.score,
        maxScore: response.data.maxScore,
        percentage: response.data.percentage,
        passed: response.data.passed,
        correctAnswers: response.data.correctAnswers,
        totalQuestions: response.data.totalQuestions
      };

      setResult(calculatedResult);
      setSubmitted(true);
    } catch (error) {
      console.error("Error submitting assessment:", error);

      // Fallback to frontend calculation if backend submission fails
      const calculatedResult = calculateScore();
      setResult(calculatedResult);
      setSubmitted(true);
    } finally {
      setLoading(false);
    }
  }

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading assessment...</span>
        </div>
      </div>
    )
  }

  if (error || !assessment) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <h3>Assessment Not Found</h3>
          <p className="text-muted">{error || "The assessment you're looking for doesn't exist."}</p>
          <Button variant="primary" onClick={() => navigate("/student-dashboard")}>
            Back to Dashboard
          </Button>
        </div>
      </Container>
    )
  }

  // If no questions or empty questions array
  if (!assessment.questions || assessment.questions.length === 0) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <h3>No Questions Available</h3>
          <p className="text-muted">This assessment doesn't have any questions yet.</p>
          <Button variant="primary" onClick={() => navigate("/student-dashboard")}>
            Back to Dashboard
          </Button>
        </div>
      </Container>
    )
  }

  if (submitted && result) {
    return (
      <Container className="py-5">
        <Row className="justify-content-center">
          <Col md={8}>
            <Card className="assessment-result">
              <Card.Header className="text-center">
                <h3>Assessment Completed!</h3>
              </Card.Header>
              <Card.Body className="text-center">
                <div className="mb-4">
                  <div className="result-circle mb-3">
                    {result.percentage}%
                  </div>
                  <h4>
                    Your Score: {result.score} / {result.maxScore}
                  </h4>
                  <p>
                    Correct Answers: {result.correctAnswers} / {result.totalQuestions}
                  </p>
                </div>

                {/* Success message - always show congratulations */}
                <Alert variant={result.passed ? "success" : "warning"}>
                  {result.passed 
                    ? "Congratulations! You passed the assessment." 
                    : "You did not pass the assessment. You need at least 60% to pass."}
                </Alert>

                <div className="d-flex justify-content-center gap-3">
                  <Button variant="primary" onClick={() => navigate("/student-dashboard")}>
                    Back to Dashboard
                  </Button>
                  <Button variant="outline-primary" onClick={() => {
                    setSubmitted(false);
                    setAnswers({});
                    setCurrentQuestion(0);
                  }}>
                    Retake Assessment
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    )
  }

  const currentQ = assessment.questions[currentQuestion]

  return (
    <Container className="py-4">
      {viewMode && (
        <div className="mb-4">
          <Button 
            variant="outline-secondary" 
            onClick={() => navigate('/assessment-management')}
          >
            ‚Üê Back to Assessment Management
          </Button>
        </div>
      )}

      <Row>
        <Col md={8}>
          <Card>
            <Card.Header className="d-flex justify-content-between align-items-center">
              <h4>{assessment.title || "Assessment"}</h4>
              <div className="d-flex align-items-center gap-3">
                <span>
                  Question {currentQuestion + 1} of {assessment.questions.length}
                </span>
              </div>
            </Card.Header>
            <Card.Body>
              <div className="assessment-question">
                <h5 className="mb-4">{currentQ.text}</h5>
                <div className="options-container">
                  {currentQ.options.map((option) => (
                    <div 
                      key={option.id}
                      className={`option-item ${answers[currentQ.id] === option.id ? 'selected' : ''}`}
                      onClick={() => handleAnswerChange(currentQ.id, option.id)}
                    >
                      <div className="option-content">
                        <div className="radio-circle">
                          {answers[currentQ.id] === option.id && <div className="radio-dot"></div>}
                        </div>
                        <span className="option-text">{option.text}</span>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="d-flex justify-content-end mt-3">
                  <Button 
                    variant="outline-secondary" 
                    size="sm" 
                    onClick={clearCurrentAnswer}
                    disabled={!answers[currentQ.id]}
                  >
                    Clear Answer
                  </Button>
                </div>
              </div>

              <div className="d-flex justify-content-between mt-4">
                <Button
                  variant="outline-secondary"
                  disabled={currentQuestion === 0}
                  onClick={() => setCurrentQuestion(currentQuestion - 1)}
                >
                  Previous
                </Button>

                {currentQuestion === assessment.questions.length - 1 ? (
                  <Button variant="success" onClick={handleSubmit}>
                    Submit Assessment
                  </Button>
                ) : (
                  <Button variant="primary" onClick={() => setCurrentQuestion(currentQuestion + 1)}>
                    Next
                  </Button>
                )}
              </div>
            </Card.Body>
          </Card>
        </Col>

        <Col md={4}>
          <Card>
            <Card.Header>
              <h5 className="mb-0">Assessment Progress</h5>
            </Card.Header>
            <Card.Body>
              <div className="mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Progress</span>
                  <span>{Math.round(((currentQuestion + 1) / assessment.questions.length) * 100)}%</span>
                </div>
                <div className="progress">
                  <div
                    className="progress-bar"
                    style={{ width: `${((currentQuestion + 1) / assessment.questions.length) * 100}%` }}
                  ></div>
                </div>
              </div>

              <div className="mb-3">
                <strong>Questions Answered:</strong>
                <span className="ms-2">
                  {Object.keys(answers).length} / {assessment.questions.length}
                </span>
              </div>

              <div className="mb-3">
                <strong>Max Score:</strong>
                <span className="ms-2">{assessment.maxScore || 100}</span>
              </div>
            </Card.Body>
          </Card>

          <Card className="mt-3">
            <Card.Header>
              <h5 className="mb-0">Question Navigator</h5>
            </Card.Header>
            <Card.Body>
              <div className="d-flex flex-wrap gap-2 question-navigator">
                {assessment.questions.map((_, index) => (
                  <Button
                    key={index}
                    variant={
                      index === currentQuestion
                        ? "primary"
                        : answers[assessment.questions[index].id]
                          ? "success"
                          : "outline-secondary"
                    }
                    size="sm"
                    onClick={() => setCurrentQuestion(index)}
                  >
                    {index + 1}
                  </Button>
                ))}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  )
}

export default AssessmentPage
