"use client"

import { useState, useEffect } from "react"
import { Container, Row, Col, Card, Button, Table, Modal, Form, Tab, Tabs } from "react-bootstrap"
import { courseService, userService, assessmentService } from "../services/api"
import { useAuth } from "../contexts/AuthContext"
import { Link } from "react-router-dom"

const InstructorDashboard = () => {
  const [courses, setCourses] = useState([])
  const [stats, setStats] = useState({})
  const [assessments, setAssessments] = useState([])
  const [showCourseModal, setShowCourseModal] = useState(false)
  const [showAssessmentModal, setShowAssessmentModal] = useState(false)
  const [editingCourse, setEditingCourse] = useState(null)
  const [loading, setLoading] = useState(true)
  const { user } = useAuth()

  const [courseForm, setCourseForm] = useState({
    title: "",
    description: "",
    mediaUrl: "",
  })

  const [assessmentForm, setAssessmentForm] = useState({
    title: "",
    courseId: "",
    maxScore: 100,
    questions: [{ text: "", options: ["", "", "", ""], correctOption: 0 }]
  })

  useEffect(() => {
    fetchDashboardData()
  }, [])

  const fetchDashboardData = async () => {
    try {
      const [coursesResponse, statsResponse, assessmentsResponse] = await Promise.all([
        courseService.getInstructorCourses(),
        userService.getInstructorStats(),
        assessmentService.getAllAssessments()
      ])

      // Get the courses and assessments
      const fetchedCourses = coursesResponse.data || [];
      const fetchedAssessments = assessmentsResponse.data || [];

      // Calculate assessment counts for each course
      const coursesWithCounts = fetchedCourses.map(course => {
        const assessmentCount = fetchedAssessments.filter(
          assessment => assessment.courseId === course.courseId
        ).length;

        return {
          ...course,
          assessmentCount
        };
      });

      setCourses(coursesWithCounts)
      setStats(statsResponse.data)
      setAssessments(assessmentsResponse.data || [])
    } catch (error) {
      console.error("Error fetching dashboard data:", error)
    } finally {
      setLoading(false)
    }
  }

  const handleCourseSubmit = async (e) => {
    e.preventDefault()
    try {
      if (editingCourse) {
        await courseService.updateCourse(editingCourse.courseId, courseForm)
      } else {
        await courseService.createCourse(courseForm)
      }

      setShowCourseModal(false)
      setCourseForm({ title: "", description: "", mediaUrl: "" })
      setEditingCourse(null)
      fetchDashboardData()
    } catch (error) {
      console.error("Error saving course:", error)
    }
  }

  const handleEditCourse = (course) => {
    setEditingCourse(course)
    setCourseForm({
      title: course.title,
      description: course.description,
      mediaUrl: course.mediaUrl || "",
    })
    setShowCourseModal(true)
  }

  const handleDeleteCourse = async (courseId) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      try {
        await courseService.deleteCourse(courseId)
        fetchDashboardData()
      } catch (error) {
        console.error("Error deleting course:", error)
      }
    }
  }

  const handleAssessmentSubmit = async (e) => {
    e.preventDefault()
    try {
      // Convert questions to JSON string for backend
      const formattedAssessment = {
        title: assessmentForm.title,
        courseId: assessmentForm.courseId,
        maxScore: assessmentForm.maxScore,
        questions: JSON.stringify(assessmentForm.questions)
      }

      await assessmentService.createAssessment(formattedAssessment)
      setShowAssessmentModal(false)
      setAssessmentForm({
        title: "",
        courseId: "",
        maxScore: 100,
        questions: [{ text: "", options: ["", "", "", ""], correctOption: 0 }]
      })
      fetchDashboardData()
    } catch (error) {
      console.error("Error saving assessment:", error)
    }
  }

  const addQuestion = () => {
    setAssessmentForm({
      ...assessmentForm,
      questions: [
        ...assessmentForm.questions,
        { text: "", options: ["", "", "", ""], correctOption: 0 }
      ]
    })
  }

  const removeQuestion = (index) => {
    const updatedQuestions = [...assessmentForm.questions]
    updatedQuestions.splice(index, 1)
    setAssessmentForm({
      ...assessmentForm,
      questions: updatedQuestions
    })
  }

  const updateQuestion = (index, field, value) => {
    const updatedQuestions = [...assessmentForm.questions]
    if (field === "option") {
      const [optionIndex, optionValue] = value
      updatedQuestions[index].options[optionIndex] = optionValue
    } else if (field === "correctOption") {
      updatedQuestions[index].correctOption = parseInt(value)
    } else {
      updatedQuestions[index][field] = value
    }

    setAssessmentForm({
      ...assessmentForm,
      questions: updatedQuestions
    })
  }

  const handleDeleteAssessment = async (assessmentId) => {
    if (window.confirm("Are you sure you want to delete this assessment?")) {
      try {
        await assessmentService.deleteAssessment(assessmentId);
        fetchDashboardData();
      } catch (error) {
        console.error("Error deleting assessment:", error);
      }
    }
  };

  const handleViewAssessment = (assessmentId) => {
    // Navigate to assessment detail page
    window.location.href = `/assessment/${assessmentId}`;
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    )
  }

  return (
    <Container fluid className="py-4">
      <Row>
        <Col>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <h1 className="h3 mb-0">Instructor Dashboard</h1>
              <p className="text-muted">Welcome back, {user?.name}!</p>
            </div>
            <Button variant="primary" onClick={() => setShowCourseModal(true)}>
              Create New Course
            </Button>
          </div>

          {/* Stats Cards */}
          <Row className="mb-4">
            <Col md={3}>
              <Card className="stats-card text-center">
                <Card.Body>
                  <h3 className="mb-0">{courses.length}</h3>
                  <small>Total Courses</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="stats-card text-center">
                <Card.Body>
                  <h3 className="mb-0">{stats.totalStudents || 0}</h3>
                  <small>Total Students</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="stats-card text-center">
                <Card.Body>
                  <h3 className="mb-0">{stats.totalAssessments || 0}</h3>
                  <small>Assessments Created</small>
                </Card.Body>
              </Card>
            </Col>
            <Col md={3}>
              <Card className="stats-card text-center">
                <Card.Body>
                  <h3 className="mb-0">{stats.pendingGrading || 0}</h3>
                  <small>Pending Grading</small>
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* Main Content */}
          <Tabs defaultActiveKey="courses" className="mb-4">
            <Tab eventKey="courses" title="My Courses">
              <Card>
                <Card.Header>
                  <h5 className="mb-0">Course Management</h5>
                </Card.Header>
                <Card.Body>
                  {courses.length === 0 ? (
                    <div className="text-center py-5">
                      <h5>No Courses Created</h5>
                      <p className="text-muted">Create your first course to get started!</p>
                      <Button variant="primary" onClick={() => setShowCourseModal(true)}>
                        Create Course
                      </Button>
                    </div>
                  ) : (
                    <Table responsive>
                      <thead>
                        <tr>
                          <th style={{ width: '25%', textAlign: 'center' }}>Course Title</th>
                          <th style={{ width: '25%', textAlign: 'center' }}>Students Enrolled</th>
                          <th style={{ width: '25%', textAlign: 'center' }}>Assessments</th>
                          <th style={{ width: '25%', textAlign: 'center' }}>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {courses.map((course) => (
                          <tr key={course.courseId}>
                            <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                              <div>
                                <strong>{course.title}</strong>
                                <br />
                                <small className="text-muted">{course.description?.substring(0, 50)}...</small>
                              </div>
                            </td>
                            <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>{course.enrolledStudents || 0}</td>
                            <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>{course.assessmentCount || 0}</td>
                            <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                              <div className="d-flex gap-2 justify-content-center">
                                <Link to={`/course-management/${course.courseId}`}>
                                  <Button variant="primary" size="sm">
                                    Manage
                                  </Button>
                                </Link>
                                <Button variant="outline-primary" size="sm" onClick={() => handleEditCourse(course)}>
                                  Edit
                                </Button>
                                <Button
                                  variant="outline-danger"
                                  size="sm"
                                  onClick={() => handleDeleteCourse(course.courseId)}
                                >
                                  Delete
                                </Button>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  )}
                </Card.Body>
              </Card>
            </Tab>

            <Tab eventKey="assessments" title="Assessments">
              <Card>
                <Card.Header>
                  <h5 className="mb-0">Assessment Management</h5>
                </Card.Header>
                <Card.Body>
                  {assessments.length === 0 ? (
                    <div className="text-center py-5">
                      <h5>No Assessments Created</h5>
                      <p className="text-muted">Create your first assessment to get started!</p>
                      <Button variant="primary" onClick={() => setShowAssessmentModal(true)}>
                        Create Assessment
                      </Button>
                    </div>
                  ) : (
                    <>
                      <div className="d-flex justify-content-end mb-3">
                        <Button variant="primary" onClick={() => setShowAssessmentModal(true)}>
                          Create Assessment
                        </Button>
                      </div>
                      <Table responsive>
                        <thead>
                          <tr>
                            <th style={{ textAlign: 'center' }}>Title</th>
                            <th style={{ textAlign: 'center' }}>Course</th>
                            <th style={{ textAlign: 'center' }}>Max Score</th>
                            <th style={{ textAlign: 'center' }}>Questions</th>
                            <th style={{ textAlign: 'center' }}>Actions</th>
                          </tr>
                        </thead>
                        <tbody>
                          {assessments.map((assessment) => (
                            <tr key={assessment.assessmentId}>
                              <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>{assessment.title}</td>
                              <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                                {courses.find(c => c.courseId === assessment.courseId)?.title || 'Unknown Course'}
                              </td>
                              <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>{assessment.maxScore}</td>
                              <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                                {assessment.questions ? 
                                  (typeof assessment.questions === 'string' ? 
                                    JSON.parse(assessment.questions).length : 
                                    assessment.questions.length) : 
                                  0} questions
                              </td>
                              <td style={{ textAlign: 'center', verticalAlign: 'middle' }}>
                                <div className="d-flex gap-2 justify-content-center">
                                  <Button 
                                    variant="outline-primary" 
                                    size="sm" 
                                    onClick={() => handleViewAssessment(assessment.assessmentId)}
                                  >
                                    View
                                  </Button>
                                  <Button 
                                    variant="outline-danger" 
                                    size="sm" 
                                    onClick={() => handleDeleteAssessment(assessment.assessmentId)}
                                  >
                                    Delete
                                  </Button>
                                </div>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </Table>
                    </>
                  )}
                </Card.Body>
              </Card>
            </Tab>

            <Tab eventKey="students" title="Students">
              <Card>
                <Card.Header>
                  <h5 className="mb-0">Student Management</h5>
                </Card.Header>
                <Card.Body>
                  <div className="text-center py-5">
                    <h5>Student Overview</h5>
                    <p className="text-muted">View and manage students enrolled in your courses</p>
                  </div>
                </Card.Body>
              </Card>
            </Tab>
          </Tabs>
        </Col>
      </Row>

      {/* Course Modal */}
      <Modal show={showCourseModal} onHide={() => setShowCourseModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>{editingCourse ? "Edit Course" : "Create New Course"}</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleCourseSubmit}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>Course Title</Form.Label>
              <Form.Control
                type="text"
                value={courseForm.title}
                onChange={(e) => setCourseForm({ ...courseForm, title: e.target.value })}
                placeholder="Enter course title"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                value={courseForm.description}
                onChange={(e) => setCourseForm({ ...courseForm, description: e.target.value })}
                placeholder="Enter course description"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Course Media URL (Optional)</Form.Label>
              <Form.Control
                type="url"
                value={courseForm.mediaUrl}
                onChange={(e) => setCourseForm({ ...courseForm, mediaUrl: e.target.value })}
                placeholder="Enter image URL"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Upload Course Materials</Form.Label>
              <div className="file-upload-area">
                <i className="bi bi-cloud-upload fs-1 text-muted"></i>
                <p className="mt-2 mb-0">Drag and drop files here or click to browse</p>
                <small className="text-muted">Supported formats: MP4, JPG, PNG, PDF</small>
                <Form.Control type="file" multiple accept=".mp4,.jpg,.jpeg,.png,.pdf" className="mt-2" />
              </div>
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowCourseModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              {editingCourse ? "Update Course" : "Create Course"}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* Assessment Modal */}
      <Modal show={showAssessmentModal} onHide={() => setShowAssessmentModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Create New Assessment</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleAssessmentSubmit}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>Assessment Title</Form.Label>
              <Form.Control
                type="text"
                value={assessmentForm.title}
                onChange={(e) => setAssessmentForm({ ...assessmentForm, title: e.target.value })}
                placeholder="Enter assessment title"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Max Score</Form.Label>
              <Form.Control
                type="number"
                min="1"
                max="100"
                value={assessmentForm.maxScore}
                onChange={(e) => setAssessmentForm({ ...assessmentForm, maxScore: parseInt(e.target.value) })}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Select Course</Form.Label>
              <Form.Select
                value={assessmentForm.courseId}
                onChange={(e) => setAssessmentForm({ ...assessmentForm, courseId: e.target.value })}
                required
              >
                <option value="">Select a course</option>
                {courses.map((course) => (
                  <option key={course.courseId} value={course.courseId}>
                    {course.title}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>

            <hr className="my-4" />
            <h5>Questions</h5>

            {assessmentForm.questions.map((question, qIndex) => (
              <div key={qIndex} className="assessment-question mb-4 p-3 border rounded">
                <div className="d-flex justify-content-between mb-2">
                  <h6>Question {qIndex + 1}</h6>
                  {assessmentForm.questions.length > 1 && (
                    <Button 
                      variant="outline-danger" 
                      size="sm"
                      onClick={() => removeQuestion(qIndex)}
                    >
                      Remove
                    </Button>
                  )}
                </div>

                <Form.Group className="mb-3">
                  <Form.Control
                    type="text"
                    value={question.text}
                    onChange={(e) => updateQuestion(qIndex, "text", e.target.value)}
                    placeholder="Enter question text"
                    required
                  />
                </Form.Group>

                <div className="mb-3">
                  <Form.Label>Options</Form.Label>
                  {question.options.map((option, oIndex) => (
                    <div key={oIndex} className="d-flex mb-2 align-items-center">
                      <Form.Check
                        type="radio"
                        name={`correct-option-${qIndex}`}
                        checked={question.correctOption === oIndex}
                        onChange={() => updateQuestion(qIndex, "correctOption", oIndex)}
                        className="me-2"
                      />
                      <Form.Control
                        type="text"
                        value={option}
                        onChange={(e) => updateQuestion(qIndex, "option", [oIndex, e.target.value])}
                        placeholder={`Option ${oIndex + 1}`}
                        required
                      />
                    </div>
                  ))}
                </div>
              </div>
            ))}

            <div className="text-center">
              <Button variant="outline-primary" onClick={addQuestion}>
                Add Question
              </Button>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowAssessmentModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              Create Assessment
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  )
}

export default InstructorDashboard
