import { useState, useEffect } from "react";
import { Container, Row, Col, Card, Button, Form, Nav, Alert, Table } from "react-bootstrap";
import { useParams, useNavigate } from "react-router-dom";
import { courseService, courseMaterialService, assessmentService } from "../services/api";
import { useAuth } from "../contexts/AuthContext";
import CourseMaterialsManager from "../components/CourseMaterialsManager";

const CourseManagementPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [course, setCourse] = useState(null);
  const [materials, setMaterials] = useState([]);
  const [assessments, setAssessments] = useState([]);
  const [activeTab, setActiveTab] = useState("details");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  
  const [courseForm, setCourseForm] = useState({
    title: "",
    description: "",
    mediaUrl: ""
  });

  useEffect(() => {
    fetchCourseData();
  }, [id]);

  const fetchCourseData = async () => {
    setIsLoading(true);
    try {
      // Get course details
      const courseResponse = await courseService.getCourseById(id);
      const courseData = courseResponse.data;
      setCourse(courseData);
      setCourseForm({
        title: courseData.title || "",
        description: courseData.description || "",
        mediaUrl: courseData.mediaUrl || ""
      });

      // Get course materials
      const materialsResponse = await courseMaterialService.getMaterials(id);
      setMaterials(materialsResponse.data || []);
      
      // Get course assessments
      const assessmentsResponse = await assessmentService.getAssessmentsByCourse(id);
      setAssessments(assessmentsResponse.data || []);
    } catch (error) {
      console.error("Error fetching course data:", error);
      setError("Failed to load course data. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleCourseUpdate = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");
    setSuccess("");

    try {
      await courseService.updateCourse(id, courseForm);
      setSuccess("Course updated successfully!");
      fetchCourseData(); // Refresh data
    } catch (error) {
      console.error("Error updating course:", error);
      setError("Failed to update course. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCourseForm({
      ...courseForm,
      [name]: value
    });
  };

  const handleMaterialsUpdate = (updatedMaterials) => {
    setMaterials(updatedMaterials);
  };
  
  const handleViewAssessment = (assessmentId) => {
    navigate(`/assessment/edit/${assessmentId}`);
  };
  
  const handleDeleteAssessment = async (assessmentId) => {
    if (window.confirm("Are you sure you want to delete this assessment?")) {
      try {
        await assessmentService.deleteAssessment(assessmentId);
        // Refresh assessments after deletion
        const response = await assessmentService.getAssessmentsByCourse(id);
        setAssessments(response.data || []);
        setSuccess("Assessment deleted successfully!");
      } catch (error) {
        console.error("Error deleting assessment:", error);
        setError("Failed to delete assessment. Please try again.");
      }
    }
  };
  
  const handleCreateAssessment = () => {
    navigate(`/assessment/create?courseId=${id}`);
  };

  // Ensure only instructors who own this course can access
  useEffect(() => {
    if (!isLoading && course && user) {
      if (user.role !== "Instructor" || course.instructorId !== user.userId) {
        navigate("/instructor-dashboard");
      }
    }
  }, [course, user, isLoading, navigate]);

  if (isLoading) {
    return (
      <Container className="py-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </Container>
    );
  }

  if (!course) {
    return (
      <Container className="py-5">
        <Alert variant="danger">Course not found or you don't have permission to manage it.</Alert>
        <Button variant="primary" onClick={() => navigate("/instructor-dashboard")}>
          Back to Dashboard
        </Button>
      </Container>
    );
  }

  return (
    <Container className="py-5">
      <Row className="mb-4">
        <Col>
          <h2>Manage Course: {course.title}</h2>
          <p className="text-muted">Use this page to update course details and manage materials</p>
        </Col>
      </Row>

      <Row>
        <Col md={3}>
          <Nav variant="pills" className="flex-column mb-4" activeKey={activeTab}>
            <Nav.Item>
              <Nav.Link 
                eventKey="details" 
                onClick={() => setActiveTab("details")}
              >
                Course Details
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link 
                eventKey="materials" 
                onClick={() => setActiveTab("materials")}
              >
                Course Materials
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link 
                eventKey="assessments" 
                onClick={() => setActiveTab("assessments")}
              >
                Assessments
              </Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link 
                eventKey="students" 
                onClick={() => setActiveTab("students")}
              >
                Enrolled Students
              </Nav.Link>
            </Nav.Item>
          </Nav>
          
          <Card className="mb-4">
            <Card.Body>
              <Button 
                variant="outline-primary" 
                className="w-100 mb-2"
                onClick={() => navigate(`/course/${id}`)}
              >
                View Course Page
              </Button>
              <Button 
                variant="outline-secondary" 
                className="w-100"
                onClick={() => navigate("/instructor-dashboard")}
              >
                Back to Dashboard
              </Button>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={9}>
          {error && <Alert variant="danger">{error}</Alert>}
          {success && <Alert variant="success">{success}</Alert>}
          
          {activeTab === "details" && (
            <Card>
              <Card.Header>
                <h5 className="mb-0">Course Details</h5>
              </Card.Header>
              <Card.Body>
                <Form onSubmit={handleCourseUpdate}>
                  <Form.Group className="mb-3">
                    <Form.Label>Course Title</Form.Label>
                    <Form.Control
                      type="text"
                      name="title"
                      value={courseForm.title}
                      onChange={handleInputChange}
                      placeholder="Enter course title"
                      required
                    />
                  </Form.Group>
                  
                  <Form.Group className="mb-3">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={5}
                      name="description"
                      value={courseForm.description}
                      onChange={handleInputChange}
                      placeholder="Enter course description"
                      required
                    />
                  </Form.Group>
                  
                  <Form.Group className="mb-3">
                    <Form.Label>Course Image URL</Form.Label>
                    <Form.Control
                      type="url"
                      name="mediaUrl"
                      value={courseForm.mediaUrl}
                      onChange={handleInputChange}
                      placeholder="Enter image URL"
                    />
                    <Form.Text className="text-muted">
                      Enter a URL to an image that represents your course
                    </Form.Text>
                  </Form.Group>
                  
                  <Button 
                    type="submit" 
                    variant="primary"
                    disabled={isLoading}
                  >
                    {isLoading ? "Saving..." : "Save Changes"}
                  </Button>
                </Form>
              </Card.Body>
            </Card>
          )}
          
          {activeTab === "materials" && (
            <Card>
              <Card.Header>
                <h5 className="mb-0">Course Materials</h5>
              </Card.Header>
              <Card.Body>
                <CourseMaterialsManager 
                  courseId={id}
                  existingMaterials={materials}
                  onMaterialsUpdate={handleMaterialsUpdate}
                />
              </Card.Body>
            </Card>
          )}
          
          {activeTab === "assessments" && (
            <Card>
              <Card.Header className="d-flex justify-content-between align-items-center">
                <h5 className="mb-0">Course Assessments</h5>
                <Button variant="primary" onClick={handleCreateAssessment}>
                  Create Assessment
                </Button>
              </Card.Header>
              <Card.Body>
                {assessments.length === 0 ? (
                  <div className="text-center py-3">
                    <p className="text-muted mb-4">No assessments created for this course yet.</p>
                    <Button variant="primary" onClick={handleCreateAssessment}>
                      Create Your First Assessment
                    </Button>
                  </div>
                ) : (
                  <Table responsive>
                    <thead>
                      <tr>
                        <th>Title</th>
                        <th>Max Score</th>
                        <th>Questions</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {assessments.map((assessment) => (
                        <tr key={assessment.assessmentId}>
                          <td>{assessment.title}</td>
                          <td>{assessment.maxScore}</td>
                          <td>
                            {assessment.questions ? 
                              (typeof assessment.questions === 'string' ? 
                                JSON.parse(assessment.questions).length : 
                                assessment.questions.length) : 
                              0} questions
                          </td>
                          <td>
                            <div className="d-flex gap-2">
                              <Button 
                                variant="outline-primary" 
                                size="sm"
                                onClick={() => handleViewAssessment(assessment.assessmentId)}
                              >
                                Edit
                              </Button>
                              <Button 
                                variant="outline-primary" 
                                size="sm"
                                onClick={() => navigate(`/assessment/${assessment.assessmentId}?mode=preview`)}
                              >
                                Preview
                              </Button>
                              <Button 
                                variant="outline-danger" 
                                size="sm"
                                onClick={() => handleDeleteAssessment(assessment.assessmentId)}
                              >
                                Delete
                              </Button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                )}
              </Card.Body>
            </Card>
          )}
          
          {activeTab === "students" && (
            <Card>
              <Card.Header>
                <h5 className="mb-0">Enrolled Students</h5>
              </Card.Header>
              <Card.Body>
                <p className="text-muted">
                  This feature is coming soon. You'll be able to view and manage students enrolled in this course.
                </p>
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default CourseManagementPage;
