"use client"

import { useState, useEffect } from "react"
import { Container, Row, Col, Card, Button, Form, Alert } from "react-bootstrap"
import { useParams, useNavigate } from "react-router-dom"
import { assessmentService } from "../services/api"

const AssessmentPage = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const [assessment, setAssessment] = useState(null)
  const [answers, setAnswers] = useState({})
  const [currentQuestion, setCurrentQuestion] = useState(0)
  const [timeRemaining, setTimeRemaining] = useState(null)
  const [submitted, setSubmitted] = useState(false)
  const [result, setResult] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetchAssessment()
  }, [id])

  useEffect(() => {
    if (timeRemaining > 0) {
      const timer = setTimeout(() => {
        setTimeRemaining(timeRemaining - 1)
      }, 1000)
      return () => clearTimeout(timer)
    } else if (timeRemaining === 0) {
      handleSubmit()
    }
  }, [timeRemaining])

  const fetchAssessment = async () => {
    try {
      const response = await assessmentService.getAssessmentById(id)
      setAssessment(response.data)
      setTimeRemaining(response.data.timeLimit * 60) // Convert minutes to seconds
    } catch (error) {
      console.error("Error fetching assessment:", error)
    } finally {
      setLoading(false)
    }
  }

  const handleAnswerChange = (questionId, optionId) => {
    setAnswers({
      ...answers,
      [questionId]: optionId,
    })
  }

  const handleSubmit = async () => {
    try {
      const response = await assessmentService.submitAssessment(id, answers)
      setResult(response.data)
      setSubmitted(true)
    } catch (error) {
      console.error("Error submitting assessment:", error)
    }
  }

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60)
    const remainingSeconds = seconds % 60
    return `${minutes}:${remainingSeconds.toString().padStart(2, "0")}`
  }

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading assessment...</span>
        </div>
      </div>
    )
  }

  if (submitted && result) {
    return (
      <Container className="py-5">
        <Row className="justify-content-center">
          <Col md={8}>
            <Card>
              <Card.Header className="text-center">
                <h3>Assessment Completed!</h3>
              </Card.Header>
              <Card.Body className="text-center">
                <div className="mb-4">
                  <div className="progress-circle bg-success mb-3">
                    {Math.round((result.score / assessment.maxScore) * 100)}%
                  </div>
                  <h4>
                    Your Score: {result.score} / {assessment.maxScore}
                  </h4>
                </div>
                <Alert variant={result.passed ? "success" : "warning"}>
                  {result.passed
                    ? "Congratulations! You passed the assessment."
                    : "You didn't pass this time. Keep studying and try again!"}
                </Alert>
                <div className="d-flex justify-content-center gap-3">
                  <Button variant="primary" onClick={() => navigate("/student-dashboard")}>
                    Back to Dashboard
                  </Button>
                  <Button variant="outline-primary" onClick={() => window.location.reload()}>
                    Retake Assessment
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    )
  }

  if (!assessment) {
    return (
      <Container className="py-5">
        <div className="text-center">
          <h3>Assessment Not Found</h3>
          <Button variant="primary" onClick={() => navigate("/student-dashboard")}>
            Back to Dashboard
          </Button>
        </div>
      </Container>
    )
  }

  const currentQ = assessment.questions[currentQuestion]

  return (
    <Container className="py-5">
      <Row>
        <Col md={8}>
          <Card>
            <Card.Header className="d-flex justify-content-between align-items-center">
              <h4>{assessment.title}</h4>
              <div className="d-flex align-items-center gap-3">
                <span>Time Remaining: {formatTime(timeRemaining)}</span>
                <span>
                  Question {currentQuestion + 1} of {assessment.questions.length}
                </span>
              </div>
            </Card.Header>
            <Card.Body>
              <div className="assessment-question">
                <h5 className="mb-3">{currentQ.questionText}</h5>
                <Form>
                  {currentQ.options.map((option) => (
                    <Form.Check
                      key={option.id}
                      type="radio"
                      name={`question-${currentQ.id}`}
                      id={`option-${option.id}`}
                      label={option.text}
                      checked={answers[currentQ.id] === option.id}
                      onChange={() => handleAnswerChange(currentQ.id, option.id)}
                      className="option-item"
                    />
                  ))}
                </Form>
              </div>

              <div className="d-flex justify-content-between mt-4">
                <Button
                  variant="outline-secondary"
                  disabled={currentQuestion === 0}
                  onClick={() => setCurrentQuestion(currentQuestion - 1)}
                >
                  Previous
                </Button>

                {currentQuestion === assessment.questions.length - 1 ? (
                  <Button variant="success" onClick={handleSubmit}>
                    Submit Assessment
                  </Button>
                ) : (
                  <Button variant="primary" onClick={() => setCurrentQuestion(currentQuestion + 1)}>
                    Next
                  </Button>
                )}
              </div>
            </Card.Body>
          </Card>
        </Col>

        <Col md={4}>
          <Card>
            <Card.Header>
              <h5 className="mb-0">Assessment Progress</h5>
            </Card.Header>
            <Card.Body>
              <div className="mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Progress</span>
                  <span>{Math.round(((currentQuestion + 1) / assessment.questions.length) * 100)}%</span>
                </div>
                <div className="progress">
                  <div
                    className="progress-bar"
                    style={{ width: `${((currentQuestion + 1) / assessment.questions.length) * 100}%` }}
                  ></div>
                </div>
              </div>

              <div className="mb-3">
                <strong>Questions Answered:</strong>
                <span className="ms-2">
                  {Object.keys(answers).length} / {assessment.questions.length}
                </span>
              </div>

              <div className="mb-3">
                <strong>Max Score:</strong>
                <span className="ms-2">{assessment.maxScore}</span>
              </div>
            </Card.Body>
          </Card>

          <Card className="mt-3">
            <Card.Header>
              <h5 className="mb-0">Question Navigator</h5>
            </Card.Header>
            <Card.Body>
              <div className="d-flex flex-wrap gap-2">
                {assessment.questions.map((_, index) => (
                  <Button
                    key={index}
                    variant={
                      index === currentQuestion
                        ? "primary"
                        : answers[assessment.questions[index].id]
                          ? "success"
                          : "outline-secondary"
                    }
                    size="sm"
                    onClick={() => setCurrentQuestion(index)}
                  >
                    {index + 1}
                  </Button>
                ))}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  )
}

export default AssessmentPage
